import './App.css';
import MovieCard from './MovieCard';
import React, { useEffect, useState } from 'react'
//7e2503f4

const API_URL = "http://www.omdbapi.com?apikey=7e2503f4";
function App() {

  const [flag,setflag] = useState(0);
  const [searchvalue,setsearchvalue] = useState("");
  const [movies,setmovies] = useState([]);
 const searchMovies = async (title) => {
  const response  = await fetch(`${API_URL}&s=${title}`);
  const data = await response.json();
  setmovies(data.Search);
 }

 useEffect(() => {
  //  searchMovies("superman");
 }, []);

  return (

    <div className='app'>
      <h1>SaadFlix</h1>
      <div className='search'>
      <input
      placeholder='Search Your Favourite Movies'
      value={searchvalue}
      onChange={(event)=>{setsearchvalue(event.target.value)

      }}
      />
      <img src="https://www.svgrepo.com/show/513607/search.svg" alt="search"
      onClick={()=>{searchMovies(searchvalue)
        setflag((prev)=> prev+1)
      }}
      />
      </div>
     {/* <div className='container'> <MovieCard movie={movies[0]}/></div> */}
    {
      movies?.length > 0 ? (
        <div class="container">
          {movies.map((movie)=> ( <MovieCard movie={movie} />))}
          {/* <MovieCard movie={movies[0]}/>  */}


          </div>
      ):
     ( <div className='empty'>{flag===0?(<h2>Which Movie On Your Mind Today</h2>):(<h2>Searching...if you dont find anything after 5 seconds please search something else</h2>)}</div>)
    }
    </div>

  );
}

export default App;
